"use client";

import { motion } from "framer-motion";
import { TrendingUp, TrendingDown, ArrowUpRight } from "lucide-react";
import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import AssetIcon from "@/components/AssetIcon";
import { PRIMARY_BUTTON_CLASS } from "@/lib/constants";

interface AssetCardProps {
    symbol: string;
    name: string;
    price: string;
    changePercent: string;
    isPositive: boolean;
    avatar: string;
    color?: string;
    volume: string;
    category: string;
}

export default function AssetCard({ symbol, name, price, changePercent, isPositive, avatar, color, volume, category }: AssetCardProps) {
    const [isHovered, setIsHovered] = useState(false);
    const [sparklineData, setSparklineData] = useState({ path: "", fillPath: "" });
    const router = useRouter();

    useEffect(() => {
        const pointsCount = 24;
        const points: number[] = [];
        let current = isPositive ? 60 : 40;
        for (let i = 0; i < pointsCount; i++) {
            const volatility = 8;
            const trend = isPositive ? -1.5 : 1.5;
            current += (Math.random() - 0.5) * volatility + trend;
            current = Math.max(10, Math.min(90, current));
            points.push(current);
        }

        const path = points.reduce((acc, val, i) => {
            const x = (i / (pointsCount - 1)) * 100;
            if (i === 0) return `M${x} ${val}`;
            const prevX = ((i - 1) / (pointsCount - 1)) * 100;
            const cpX = (prevX + x) / 2;
            return acc + ` C${cpX} ${points[i - 1]}, ${cpX} ${val}, ${x} ${val}`;
        }, "");

        // eslint-disable-next-line
        setSparklineData({ path, fillPath: `${path} V100 H0 Z` });
    }, [isPositive]);

    return (
        <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            onClick={() => router.push(`/discover/${symbol.toLowerCase()}`)}
            className="group relative bg-white dark:bg-slate-900 border border-slate-100 dark:border-slate-800 rounded-[28px] p-6 flex flex-col gap-5 shadow-[0_2px_8px_rgba(0,0,0,0.02)] dark:shadow-none hover:shadow-[0_30px_60px_rgba(0,0,0,0.08)] dark:hover:shadow-black/40 hover:border-slate-200 dark:hover:border-slate-700 transition-all duration-500 cursor-pointer overflow-hidden min-h-[320px]"
        >
            {/* Immersive Background Graph */}
            <div className="absolute inset-0 z-0 pointer-events-none transition-opacity duration-700">
                <svg viewBox="0 0 100 100" className="w-full h-full opacity-[0.4]" preserveAspectRatio="none">
                    <defs>
                        <linearGradient id={`bg-grad-${symbol}`} x1="0%" y1="0%" x2="0%" y2="100%">
                            <stop offset="0%" stopColor={isPositive ? "#10b981" : "#f43f5e"} stopOpacity="0.12" />
                            <stop offset="60%" stopColor={isPositive ? "#10b981" : "#f43f5e"} stopOpacity="0.04" />
                            <stop offset="100%" stopColor={isPositive ? "#10b981" : "#f43f5e"} stopOpacity="0" />
                        </linearGradient>
                    </defs>
                    <motion.path
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        d={sparklineData.fillPath}
                        fill={`url(#bg-grad-${symbol})`}
                    />
                    <motion.path
                        initial={{ pathLength: 0, opacity: 0 }}
                        animate={{ pathLength: 1, opacity: 0.2 }}
                        transition={{ duration: 2, ease: "easeOut" }}
                        d={sparklineData.path}
                        fill="none"
                        stroke={isPositive ? "#10b981" : "#f43f5e"}
                        strokeWidth="1"
                        strokeLinecap="round"
                    />
                </svg>
                <div className="absolute inset-x-0 bottom-0 h-2/3 bg-gradient-to-t from-white dark:from-slate-900 to-transparent opacity-60 z-[1]" />
            </div>

            {/* Header */}
            <div className="flex items-start justify-between relative z-10">
                <div className="flex items-center gap-3">
                    <div className="relative">
                        <AssetIcon symbol={symbol} color={color} size="md" className="transition-all duration-500" />

                    </div>
                    <div className="flex flex-col">
                        <div className="flex items-center gap-1.5">
                            <span className="text-[15px] font-black text-slate-950 dark:text-white tracking-tight leading-none">{symbol}</span>
                            <ArrowUpRight className={`w-3 h-3 text-slate-300 dark:text-slate-600 transition-colors ${isHovered ? "text-slate-950 dark:text-white" : ""}`} />
                        </div>
                        <span className="text-[10px] font-bold text-slate-400 dark:text-slate-500 mt-1 uppercase tracking-widest leading-none">{name}</span>
                    </div>
                </div>

                <div className="inline-flex items-center gap-1.5 px-2 py-1 bg-slate-50 dark:bg-slate-800 text-slate-500 dark:text-slate-400 border border-slate-100/50 dark:border-slate-700/50 rounded-md text-[9px] font-black uppercase tracking-widest leading-none">
                    {category}
                </div>
            </div>

            {/* Price Section */}
            <div className="flex flex-col gap-1 relative z-10 mt-2">
                <div className="flex items-baseline gap-1">
                    <span className="text-[32px] font-black text-slate-950 dark:text-white tracking-tighter tabular-nums leading-none">${price}</span>
                    <span className="text-[10px] font-black text-slate-400 dark:text-slate-500 uppercase tracking-widest mb-1">USD</span>
                </div>

                <div className="flex items-center gap-4 mt-1">
                    <div className={`flex items-center gap-1.5 px-2.5 py-1 rounded-lg font-black text-[11px] shadow-sm ${isPositive ? "bg-emerald-50 dark:bg-emerald-500/10 text-emerald-600 dark:text-emerald-400 border border-emerald-100/50 dark:border-emerald-800/50" : "bg-rose-50 dark:bg-rose-500/10 text-rose-600 dark:text-rose-400 border border-rose-100/50 dark:border-rose-800/50"
                        }`}>
                        {isPositive ? <TrendingUp className="w-3 h-3" /> : <TrendingDown className="w-3 h-3" />}
                        {changePercent}
                    </div>
                    <div className="flex flex-col">
                        <span className="text-[9px] font-bold text-slate-400 dark:text-slate-500 uppercase tracking-wider">Volume</span>
                        <span className="text-[11px] font-black text-slate-600 dark:text-slate-300 tabular-nums leading-none tracking-tight">${volume}</span>
                    </div>
                </div>
            </div>

            {/* Spacer to push button down */}
            <div className="flex-1 min-h-[40px]" />

            {/* Button */}
            <div className="relative z-10 pt-4">
                <button className={`w-full h-12 rounded-xl text-[11px] font-black uppercase tracking-widest ${PRIMARY_BUTTON_CLASS}`}>
                    Execute Trade
                </button>
            </div>

            {/* Dynamic Hover Accent */}
            <div className={`absolute bottom-0 left-0 h-[3px] w-full bg-slate-950 dark:bg-blue-500 transition-transform duration-500 origin-left z-20 ${isHovered ? "scale-x-100" : "scale-x-0"}`} />
        </motion.div>
    );
}

"use client";

import { motion } from "framer-motion";
import { useRouter } from "next/navigation";
import AssetIcon from "@/components/AssetIcon";
import TrendBadge from "@/components/TrendBadge";
import { TABLE_CELL_CLASS } from "@/lib/constants";

interface AssetRowProps {
    index: number;
    symbol: string;
    name: string;
    price: string;
    changeValue: string;
    changePercent: string;
    isPositive: boolean;
    avatar: string;
    volume: string;
    category: string;
    color?: string;
}

export default function AssetRow({ index, symbol, name, price, changePercent, isPositive, avatar, volume, category, color }: AssetRowProps) {
    const router = useRouter();

    return (
        <motion.tr
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.03 }}
            onClick={() => router.push(`/discover/${symbol.toLowerCase()}`)}
            className="group border-b border-slate-50 dark:border-slate-800/50 hover:bg-slate-50/80 dark:hover:bg-slate-800/50 transition-all cursor-pointer"
        >
            <td className={`${TABLE_CELL_CLASS} text-[12px] font-black w-10 md:w-16 text-center tabular-nums sticky left-0 z-10 bg-white dark:bg-slate-900 group-hover:bg-slate-50 dark:group-hover:bg-slate-800/50 transition-colors border-r border-slate-50 dark:border-slate-800/50`}>
                <span className="text-slate-400 dark:text-slate-500 group-hover:text-slate-900 dark:group-hover:text-white transition-colors">{index + 1}</span>
            </td>
            <td className={`${TABLE_CELL_CLASS} min-w-[140px] md:min-w-[200px] max-w-[160px] md:max-w-none sticky left-10 md:left-16 z-10 bg-white dark:bg-slate-900 group-hover:bg-slate-50 dark:group-hover:bg-slate-800/50 transition-colors border-r border-slate-50 dark:border-slate-800/50 shadow-[10px_0_15px_-10px_rgba(0,0,0,0.02)] dark:shadow-none`}>
                <div className="flex items-center gap-2 md:gap-3">
                    <AssetIcon symbol={symbol} color={color} size="sm" className="transition-all duration-300 group-hover:scale-110 flex-shrink-0" />
                    <div className="flex flex-col min-w-0">
                        <span className="text-[13px] font-black text-slate-950 dark:text-white leading-none tracking-tight truncate">{symbol}</span>
                        <span className="text-[10px] font-bold text-slate-400 dark:text-slate-500 mt-1.5 uppercase tracking-wider truncate">{name}</span>
                    </div>
                </div>
            </td>
            <td className={`${TABLE_CELL_CLASS} text-[14px] font-black text-slate-950 dark:text-white tabular-nums tracking-tighter`}>${price}</td>
            <td className={TABLE_CELL_CLASS}>
                <div className="inline-flex items-center gap-1.5 px-2 py-1 bg-slate-50 dark:bg-slate-800 text-slate-500 dark:text-slate-400 border border-slate-100/50 dark:border-slate-700/50 rounded-md text-[10px] font-black uppercase tracking-widest leading-none">
                    {category}
                </div>
            </td>
            <td className={TABLE_CELL_CLASS}>
                <TrendBadge isPositive={isPositive} value={changePercent} />
            </td>
            <td className={`${TABLE_CELL_CLASS} text-right text-[13px] font-bold text-slate-500 dark:text-slate-400 tabular-nums leading-none tracking-tight`}>
                <span className="text-[10px] text-slate-300 dark:text-slate-600 mr-1 uppercase">$</span>{volume}
            </td>
        </motion.tr>
    );
}

"use client";

import { motion } from "framer-motion";
import { TrendingUp, TrendingDown, ArrowRight } from "lucide-react";
import { ReactNode } from "react";
import { useRouter } from "next/navigation";
import AssetIcon from "@/components/AssetIcon";

interface StatItemProps {
    icon: string | ReactNode;
    name: string;
    symbol: string;
    value: string;
    subValue: string;
    isPositive?: boolean;
    idx?: number;
    color?: string;
}

function StatItem({ icon, name, symbol, value, subValue, isPositive, idx = 0, color }: StatItemProps) {
    const router = useRouter();

    return (
        <motion.div
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: idx * 0.05 }}
            onClick={() => router.push(`/discover/${symbol.toLowerCase()}`)}
            className="flex items-center justify-between py-4 hover:bg-white dark:hover:bg-slate-800 hover:shadow-[0_8px_30px_rgb(0,0,0,0.02)] border border-transparent hover:border-slate-100 dark:hover:border-slate-700 transition-all duration-300 rounded-[20px] px-3 cursor-pointer group"
        >
            <div className="flex items-center gap-4">
                <AssetIcon
                    symbol={symbol}
                    color={color}
                    size="sm"
                    className="transition-all duration-300"
                />
                <div className="flex flex-col">
                    <div className="flex items-center gap-2">
                        <span className="text-[13px] font-black text-slate-900 dark:text-white leading-none group-hover:text-primary transition-colors">{symbol}</span>
                        <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                            <ArrowRight className="w-3 h-3 text-primary" />
                        </div>
                    </div>
                    <span className="text-[10px] font-bold text-slate-400 dark:text-slate-500 mt-1 uppercase tracking-wider">{name}</span>
                </div>
            </div>
            <div className="flex flex-col items-end">
                <span className="text-[13px] font-black text-slate-950 dark:text-white leading-none tabular-nums">{value}</span>
                <div className={`flex items-center gap-1 mt-1 font-bold text-[10px] ${isPositive === undefined ? "text-slate-400 dark:text-slate-500" : isPositive ? "text-emerald-500 dark:text-emerald-400" : "text-rose-500 dark:text-rose-400"
                    }`}>
                    {isPositive === true && <TrendingUp className="w-2.5 h-2.5" />}
                    {isPositive === false && <TrendingDown className="w-2.5 h-2.5" />}
                    {subValue}
                </div>
            </div>
        </motion.div>
    );
}

interface StatSectionProps {
    title: string;
    items: StatItemProps[];
}

export default function StatSection({ title, items }: StatSectionProps) {
    return (
        <div className="flex flex-col bg-white/40 dark:bg-slate-900/40 backdrop-blur-md rounded-[32px] p-6 border border-white dark:border-slate-800 shadow-sm transition-colors duration-300">
            <div className="flex items-center justify-between mb-6 px-1">
                <div className="flex items-center gap-3">
                    <h3 className="text-base font-black text-slate-900 dark:text-white tracking-tight">{title}</h3>
                    <span className="text-[9px] bg-slate-900 dark:bg-blue-600 text-white px-2 py-0.5 rounded-full font-black uppercase tracking-widest">24H</span>
                </div>
            </div>
            <div className="flex flex-col gap-1">
                {items.map((item, idx) => (
                    <StatItem key={idx} {...item} idx={idx} />
                ))}
            </div>
        </div>
    );
}
